mapper.insert:
 INSERT INTO MP_MEMBER(   MNO
 					   , USERID 
					       , USERPASS 
					       , USERNAME
					       , ADDRESS 	)
               VALUES(  	 MP_MEMBER_SEQ.NEXTVAL
               		   , #{userId} 
              		   , #{userPass}
              		   , #{userName}
              		   , #{address} )

mapper.select:
SELECT USERID, USERPASS, USERNAME, grade
  FROM MP_MEMBER
WHERE USERID = #{userId}

mapper.update:
UPDATE MP_MEMBER SET 

USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
